import React, { useState, useEffect } from "react";

const slides = [
  {
    text: "Colabore com sua equipe de forma simples e segura",
    image: "https://images.pexels.com/photos/249798/pexels-photo-249798.png?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
  },
  {
    text: "Compartilhe projetos privados rapidamente",
    image: "https://images.unsplash.com/photo-1517433456452-f9633a875f6f?auto=format&fit=crop&w=1470&q=80",
  },
  {
    text: "Conecte-se com seus colegas via link curto",
    image: "https://images.unsplash.com/photo-1498050108023-c5249f4df085?auto=format&fit=crop&w=1470&q=80",
  },
];

export default function LandingPage() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % slides.length);
    }, 5000); // troca a cada 5 segundos

    return () => clearInterval(timer);
  }, []);

  return (
    <div className="min-h-screen text-[#F1F5F9] flex flex-col">
      {/* Header */}
      <header className="flex justify-center items-center px-6 py-4 border-b border-[#1E293B] bg-[#0F172A]">
        <h1 className="text-2xl font-bold text-[#38BDF8]">AcadRepository</h1>
      </header>

      {/* Hero Section */}
      <section
        className="h-[700px] flex flex-col items-center justify-center text-center px-4"
        style={{
          backgroundImage: `url(${slides[currentIndex].image})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          transition: "background-image 1s ease-in-out",
        }}
      >
        <div className="bg-black bg-opacity-60 p-8 rounded-md max-w-xl">
          <h2 className="text-4xl md:text-5xl font-bold mb-4">
            {slides[currentIndex].text}
          </h2>
          <button
            className="bg-[#38BDF8] hover:bg-[#0EA5E9] text-[#0F172A] font-semibold px-6 py-3 rounded-xl transition"
            // onClick={handleClick} pode adicionar funcionalidade aqui
          >
            Começar agora
          </button>
        </div>
      </section>

      {/* Features Section */}
      <section className="bg-[#0A0F1C] py-16 px-6">
        <div className="max-w-5xl mx-auto grid md:grid-cols-3 gap-8 text-center">
          <div>
            <h3 className="text-xl font-semibold text-[#38BDF8] mb-2">
              Comece em segundos
            </h3>
            <p className="text-[#94A3B8]">Sem burocracia ou configurações avançadas.</p>
          </div>
          <div>
            <h3 className="text-xl font-semibold text-[#38BDF8] mb-2">
              Compartilhe com um link
            </h3>
            <p className="text-[#94A3B8]">Convide colegas com um link curto e seguro.</p>
          </div>
          <div>
            <h3 className="text-xl font-semibold text-[#38BDF8] mb-2">
              Ambiente Privado
            </h3>
            <p className="text-[#94A3B8]">Mantenha seus projetos protegidos e organizados.</p>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="text-center py-6 border-t border-[#1E293B] bg-[#0F172A] text-[#64748B]">
        © 2025 AcadRepositor. Feito para universitários por universitários.
      </footer>
    </div>
  );
}
